# frozen_string_literal: true

[{ 'code' => 'C-204', 'title' => 'An Act to amend the Canadian Environmental Protection Act, 1999 (final disposal of plastic waste)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10626760', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10633126', 'introduced_date' => '2020-02-07', 'description' => 'This enactment amends the Canadian Environmental Protection Act, 1999 to prohibit the export of certain types of plastic waste to foreign countries for final disposal.' }, { 'code' => 'C-3', 'title' => 'An Act to amend the Royal Canadian Mounted Police Act and the Canada Border Services Agency Act and to make consequential amendments to other Acts', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10613928', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10615687', 'introduced_date' => '2020-01-27', 'description' => 'This enactment amends the Royal Canadian Mounted Police Act to, among other things, rename the Civilian Review and Complaints Commission for the Royal Canadian Mounted Police as the Public Complaints and Review Commission. It also amends the Canada Border Services Agency Act to, among other things, grant to that Commission powers, duties and functions in relation to the Canada Border Services Agency, including the power to conduct a review of the activities of that Agency and to investigate complaints concerning the conduct of any of that Agency’s officers or employees. It also makes consequential amendments to other Acts.' }, { 'code' => 'C-4', 'title' => 'An Act to implement the Agreement between Canada, the United States of America and the United Mexican States', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10615191', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10617993', 'introduced_date' => '2020-01-29', 'description' => 'This enactment implements the Agreement between Canada, the United States of America and the United Mexican States, done at Buenos Aires on November 30, 2018, as amended by the Protocol of Amendment to that Agreement, done at Mexico City on December 10, 2019.' }, { 'code' => 'C-203', 'title' => 'An Act to amend the National Defence Act (maiming or injuring self or another)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604049', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10628940', 'introduced_date' => '2020-02-06', 'description' => 'This enactment amends the National Defence Act to repeal the offence of maiming or injuring oneself or another person to render oneself or that other person unfit for service.' }, { 'code' => 'S-207', 'title' => 'An Act to amend the Criminal Code (disclosure of information by jurors)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10607221', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10607236', 'introduced_date' => '2019-12-12', 'description' => 'This enactment amends the Criminal Code to provide that the prohibition against the disclosure of information relating to jury proceedings does not, in certain circumstances, apply in respect of disclosure by jurors to health care professionals.' }, { 'code' => 'S-208', 'title' => 'An Act to amend the Criminal Code (independence of the judiciary)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10625187', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10625190', 'introduced_date' => '2020-02-04', 'description' => 'This enactment amends the Criminal Code to give a court the discretion to vary the punishment to be imposed in respect of an offence for which the punishment or different degrees or kinds of punishment is prescribed in an enactment.' }, { 'code' => 'S-210', 'title' => 'An Act to amend the Parliament of Canada Act (Parliamentary Visual Artist Laureate)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10625169', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10625253', 'introduced_date' => '2020-02-04', 'description' => 'This enactment creates the position of Parliamentary Visual Artist Laureate. It also corrects a reference to the Canada Council for the Arts in the English version of the Parliament of Canada Act.' }, { 'code' => 'S-213', 'title' => 'An Act to change the name of the electoral district of Châteauguay—Lacolle', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10629396', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10629408', 'introduced_date' => '2020-02-06', 'description' => 'This enactment changes the name of the electoral district of Châteauguay—Lacolle to “Châteauguay—Les Jardins-de-Napierville”.' }, { 'code' => 'S-1001', 'title' => 'An Act respecting Girl Guides of Canada', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10627060', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10627064', 'introduced_date' => '2020-02-05', 'description' => 'This enactment replaces An Act to Incorporate The Canadian Council of The Girl Guides Association, chapter 77 of the Statutes of Canada, 1917, with a new Act that continues the corporation known as “Girl Guides of Canada” and makes changes relating to its administration.' }, { 'code' => 'S-212', 'title' => 'An Act to establish International Mother Language Day', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10627090', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10627117', 'introduced_date' => '2020-02-05', 'description' => 'This enactment designates the 21st day of February in each and every year as “International Mother Language Day”.' }, { 'code' => 'S-211', 'title' => 'An Act to enact the Modern Slavery Act and to amend the Customs Tariff', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10627038', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10627049', 'introduced_date' => '2020-02-05', 'description' => "This enactment enacts the Modern Slavery Act, which imposes an obligation on certain entities to report on the measures taken\r\nto prevent and reduce the risk that forced labour or child labour\r\nis used at any step in the production of goods in Canada or elsewhere by the entity or in the production of goods imported into Canada. The Act\r\nprovides for an inspection regime and gives the Minister the\r\npower to require an entity to provide certain information." }, { 'code' => 'C-202', 'title' => 'An Act to amend the Criminal Code (assault against a health care worker)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10618751', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10624971', 'introduced_date' => '2020-02-04', 'description' => 'This enactment amends the Criminal Code to require a court to consider the fact that the victim of an assault is a health care worker to be an aggravating circumstance for the purposes of sentencing.' }, { 'code' => 'C-201', 'title' => 'An Act to develop a national school food program for children', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10618763', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10624785', 'introduced_date' => '2020-02-04', 'description' => 'This enactment provides for the development of a national school food program to ensure that all children in Canada have access to healthy food.' }, { 'code' => 'C-5', 'title' => 'An Act to amend the Judges Act and the Criminal Code', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10620333', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10623769', 'introduced_date' => '2020-02-04', 'description' => 'This enactment amends the Judges Act to restrict eligibility for judicial appointment to persons who undertake to participate in continuing education on matters related to sexual assault law and social context. It also amends the Judges Act to require that the Canadian Judicial Council report on seminars offered for the continuing education of judges on matters related to sexual assault law. Finally, it amends the Criminal Code to require that judges provide reasons for decisions in sexual assault proceedings.' }, { 'code' => 'S-209', 'title' => 'An Act to amend the Department for Women and Gender Equality Act', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10625194', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10625197', 'introduced_date' => '2020-02-04', 'description' => 'This enactment amends the Department for Women and Gender Equality Act to require the Minister for Women and Gender Equality to examine the potential effects of certain bills and amendments on women and to report to Parliament accordingly.' }, { 'code' => 'C-2', 'title' => 'An Act for granting to Her Majesty certain sums of money for the federal public administration for the fiscal year ending March 31, 2020', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604308', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10608070', 'introduced_date' => nil, 'description' => 'This enactment grants the sum of $4,855,257,827 towards defraying charges and expenses of the federal public administration for the fiscal year ending March 31, 2020 that are not otherwise provided for.' }, { 'code' => 'S-202', 'title' => 'An Act to amend the Criminal Code (conversion therapy)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604397', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10604403', 'introduced_date' => '2019-12-10', 'description' => 'This enactment amends the Criminal Code to make it an offence to advertise conversion therapy services for consideration and to obtain a financial or other material benefit for the provision of conversion therapy to a person under the age of eighteen.' }, { 'code' => 'S-203', 'title' => 'An Act to amend the National Capital Act (buildings or works of national significance)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604419', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10604421', 'introduced_date' => '2019-12-10', 'description' => 'This enactment amends the National Capital Act to establish certain requirements related to the erection, alteration, extension or demolition of certain buildings or other works within the National Capital Region.' }, { 'code' => 'S-205', 'title' => 'An Act to amend the Constitution Act, 1867 and the Parliament of Canada Act (Speaker of the Senate)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10605949', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10605951', 'introduced_date' => '2019-12-11', 'description' => 'This enactment amends the Constitution Act, 1867 to provide for the election of the Speaker and the Deputy Speaker of the Senate and their participation in votes.' }, { 'code' => 'S-206', 'title' => 'An Act to amend the Department of Public Works and Government Services Act (use of wood)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10605962', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10605976', 'introduced_date' => '2019-12-11', 'description' => 'This enactment amends the Department of Public Works and Government Services Act to require that the Minister may, in developing requirements for public works, allow the use of wood or any other thing that achieves environmental benefits.' }, { 'code' => 'S-204', 'title' => 'An Act to amend the Criminal Code and the Immigration and Refugee Protection Act (trafficking in human organs)', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604425', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10604427', 'introduced_date' => '2019-12-10', 'description' => 'This enactment amends the Criminal Code to create new offences in relation to trafficking in human organs. It also amends the Immigratiand Refugee Protection Act to provide that a permanent resident or foreign national is inadmissible to Canada if the Minister of Citizenship and Immigration is of the opinion that they have engaged in any activities relating to trafficking in human organs.' }, { 'code' => 'S-201', 'title' => 'An Act to amend the Borrowing Authority Act', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10604365', 'full_text_url' => 'https://parl.ca/DocumentViewer/en/10604382', 'introduced_date' => '2019-12-10', 'description' => 'This enactment amends the Borrowing Authority Act to limit the circumstances in which the Governor in Council may authorize the borrowing of money without legislative approval.' }, { 'code' => 'C-1', 'title' => 'An Act respecting the administration of oaths of office', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10600395', 'full_text_url' => nil, 'introduced_date' => '2019-12-05', 'description' => nil }, { 'code' => 'S-1', 'title' => 'An Act relating to railways', 'page_url' => 'https://www.parl.ca/LegisInfo/BillDetails.aspx?Language=E&billId=10601138', 'full_text_url' => nil, 'introduced_date' => '2019-12-05', 'description' => nil }]
